class String
  @@googlon_sorter = ":) lnqkgvcbxwzrhtsmdpfj"
  @@googlon_order = "lnqkgvcbxwzrhtsmdpfj"
  
  def <=>(item)
    self.googlon_order <=> item.googlon_order
  end
  
  def googlon_order
    base = @@googlon_sorter.size
    power = self.size
    
    each_char.reduce(0) do |sum, value|
      index = @@googlon_sorter.index(value)
      power = power - 1
      sum + (index*base**power)
    end
  end
  
  def googlon_number
    base = @@googlon_order.size
    power = -1
    
    each_char.reduce(0) do |sum, value|
      power = power + 1
      index = @@googlon_order.index(value)
      sum = sum + (index*base**power)
    end
  end
end

require "set"

class Array
  @@foo_letters = "dlgks"
  
  def to_googlon_numbers_a
    map do |value|
      value.googlon_number
    end
  end
  
  def items_that(operator, value)
    select do |item|
      item = item.size if item.is_a? String
      item.send(operator, value)
    end
  end
  
  def ends_with_foo
    select do |word|
      @@foo_letters.include? word[word.length-1]
    end
  end
  
  def starts_with_bar
    reject do |word|
      @@foo_letters.include? word[0]
    end
  end
  
  def without(char)
    reject do |word|
      word.include? char
    end
  end
  
  def beauty
    select do |number|
      number % 3 == 0
    end
  end
end

class Set
  def fill_in_the_blanks
    max = max_by { |v| v.length }.length
    map do |word|
      word.ljust max
    end
  end
end

dev_day = Google.new

puts "- Prepositions"
puts "--> Text A = #{dev_day.text_a.items_that(:==, 4).ends_with_foo.without("b").size}"
puts "--> Text B = #{dev_day.text_b.items_that(:==, 4).ends_with_foo.without("b").size}"
puts 

puts "- Verbs"
puts "--> Text A = #{dev_day.text_a.items_that(:>=, 6).ends_with_foo.size}"
puts "--> Text B = #{dev_day.text_b.items_that(:>=, 6).ends_with_foo.size}"
puts

puts "- Verbs in First Person"
puts "--> Text A = #{dev_day.text_a.items_that(:>=, 6).ends_with_foo.starts_with_bar.size}"
puts "--> Text B = #{dev_day.text_b.items_that(:>=, 6).ends_with_foo.starts_with_bar.size}"
puts

puts "Order"
`echo #{dev_day.vocabulary.to_set.fill_in_the_blanks.sort} > /tmp/sort.list`
puts "-- Building ordered list in /tmp/sort.list"
